        return int(a_vertexId);
    }
uniform CCMorph {
    vec4 cc_displacementWeights[15];
    vec4 cc_displacementTextureInfo;
};
    vec4 fetchFlatPixel(sampler2D tex, int pixelIndex) {
        float pixelIndexF = float(pixelIndex);
        vec2 textureResolution = vec2(float(cc_displacementTextureInfo.x), float(cc_displacementTextureInfo.y));
        float pixelX = mod(pixelIndexF, textureResolution.x);
        float pixelY = floor(pixelIndexF / textureResolution.x);
        vec2 uv = (vec2(pixelX, pixelY) + .5) / textureResolution;
        return texture(tex, uv);
    }
float getDisplacementWeight(int index) {
    float m = mod(float(index), 4.0);
    if (m < 1.0) {
        return cc_displacementWeights[index / 4].x;
    } else if (m < 2.0) {
        return cc_displacementWeights[index / 4].y;
    } else if (m < 3.0) {
        return cc_displacementWeights[index / 4].z;
    } else {
        return cc_displacementWeights[index / 4].w;
    }
