void CCSkin (inout vec4 position) {
  mat4 m = skinMatrix();
  position = m * position;
}
void CCSkin (inout StandardVertInput attr) {
  mat4 m = skinMatrix();
  attr.position = m * attr.position;
  attr.normal = (m * vec4(attr.normal, 0.0)).xyz;
  attr.tangent.xyz = (m * vec4(attr.tangent.xyz, 0.0)).xyz;
}
